ui.script:
// The context argument provides helpers like updateConfig, resolveInput, and toPowerShellLiteral.
// Update outputs by calling updateConfig('Result', toPowerShellLiteral('value')).
const { controls, updateConfig, toPowerShellLiteral, helpers } = context;
if (!controls) {
  return;
}
const button = controls.querySelector('button');
const statusEl = controls.querySelector('[data-status]');
if (!button || !statusEl) {
  return;
}
const status = helpers?.NodeUI?.bindStatus(statusEl);
let active = context.node?.config?.Result__raw === 'true';
const apply = (value) => {
  active = Boolean(value);
  const label = active ? 'ON' : 'OFF';
  button.textContent = active ? 'Turn OFF' : 'Turn ON';
  status?.set?.(`Status: ${label}`, active ? 'success' : 'info');
  updateConfig('Result__raw', active ? 'true' : 'false', { silent: true });
  updateConfig('Result', toPowerShellLiteral(active ? 'true' : 'false'));
};
apply(active);
button.addEventListener('click', () => apply(!active));
return () => {
  status?.dispose?.();
  button.replaceWith(button.cloneNode(true));
};

ui.style:
/* Styles applied to the custom UI node sample */
.custom-ui-node {
  display: inline-flex;
  align-items: center;
  gap: 0.75rem;
}
.custom-ui-node button {
  padding: 0.25rem 0.75rem;
}
.custom-ui-node [data-status] {
  font-weight: 600;
}
