ui.script:
const { node, controls, updateConfig, resolveInput, ensureAutoNodes, toPowerShellLiteral } = context;
if (!controls) return;

const grid = controls.querySelector('[data-role="grid"]');
const addBtn = controls.querySelector('[data-role="add"]');
const status = controls.querySelector('[data-role="status"]');

if (!grid || !addBtn || !status) {
  console.warn('[color_fill] Required UI elements are missing.');
  return;
}

const DEFAULT_SERVER_URL = "http://127.0.0.1:8787";
const RUN_SCRIPT_PATH = "/runScript";
const swatchSet = new Set();

/* ====== サーバ通信共通関数 ====== */
const normalizeServerUrl = (value) => {
  if (value === undefined || value === null) return "";
  let url = String(value).trim();
  if (!url) return "";
  if (!/^https?:\/\//i.test(url)) {
    url = `http://${url}`;
  }
  return url.replace(/\/+$/, "");
};

const loadServerUrl = () => {
  try {
    const stored = window?.localStorage?.getItem("nodeflow.psServerUrl");
    if (stored) {
      const normalized = normalizeServerUrl(stored);
      return normalized || DEFAULT_SERVER_URL;
    }
  } catch (error) {
    console.warn("Failed to read stored PowerShell server URL", error);
  }
  return DEFAULT_SERVER_URL;
};

const getRunScriptEndpoint = () => {
  const base = normalizeServerUrl(loadServerUrl()) || DEFAULT_SERVER_URL;
  return `${base}${RUN_SCRIPT_PATH}`;
};

const invokePowerShell = async (script) => {
  const endpoint = getRunScriptEndpoint();
  const response = await fetch(endpoint, {
    method: "POST",
    headers: { "Content-Type": "application/json; charset=utf-8" },
    body: JSON.stringify({ script }),
  });

  let payload;
  try {
    payload = await response.json();
  } catch {
    throw new Error("PowerShell サーバーから無効な応答を受信しました。");
  }

  if (!response.ok) {
    throw new Error(payload?.error || `HTTP ${response.status}`);
  }
  if (payload?.ok === false) {
    const message = Array.isArray(payload?.errors) && payload.errors.length
      ? payload.errors.join("\n")
      : payload?.error || "PowerShell がエラーを返しました。";
    throw new Error(message);
  }

  return typeof payload?.output === "string" ? payload.output.trim() : "";
};

const requestExcelJson = async (script) => {
  const output = await invokePowerShell(script);
  if (!output) return null;
  try {
    return JSON.parse(output);
  } catch {
    throw new Error("PowerShell の応答を解析できませんでした。");
  }
};

/* ====== 共通UI関数 ====== */
const setStatus = (message, state = "") => {
  status.textContent = message || "";
  status.dataset.state = state;
};

const isEmptyValue = (value) =>
  value === undefined ||
  value === null ||
  (typeof value === "string" && !value.trim());

/* ====== 値解析ユーティリティ ====== */
const tryParseStructured = (value) => {
  if (typeof value !== "string") return value;
  const trimmed = value.trim();
  if (!trimmed) return "";
  if (
    (trimmed.startsWith("{") && trimmed.endsWith("}")) ||
    (trimmed.startsWith("[") && trimmed.endsWith("]")) ||
    /^(true|false|null)$/i.test(trimmed)
  ) {
    try {
      return JSON.parse(trimmed);
    } catch {
      // fall through
    }
  }
  return value;
};

const isPlainObject = (value) =>
  Object.prototype.toString.call(value) === "[object Object]";

const getPortValue = async (name) => {
  const targets = Array.isArray(name) ? name.filter(Boolean) : [name].filter(Boolean);
  if (typeof ensureAutoNodes === "function" && targets.length) {
    try {
      await ensureAutoNodes(targets);
    } catch (error) {
      console.warn(`[color_fill] Failed to auto-update input ${targets.join(", ")}`, error);;
      throw error;
    }
  }

  if (typeof resolveInput === "function") {
    try {
      const raw = resolveInput(name, { preferRaw: true });
      if (!isEmptyValue(raw)) return raw;
      const resolved = resolveInput(name);
      if (!isEmptyValue(resolved)) return resolved;
    } catch (error) {
      console.warn(`[color_fill] Failed to resolve input ${name}`, error);
    }
  }

  const rawConfig = node?.config?.[`${name}__raw`];
  if (!isEmptyValue(rawConfig)) return rawConfig;

  const stored = node?.config?.[name];
  if (!isEmptyValue(stored)) return stored;

  return "";
};

/* ====== 色ユーティリティ ====== */
const clampChannel = (value) => {
  const num = Number(value);
  if (!Number.isFinite(num)) {
    throw new Error("RGB の値が数値ではありません。");
  }
  return Math.max(0, Math.min(255, Math.round(num)));
};

const rgbToHex = (r, g, b) =>
  "#" + [r, g, b].map((v) => v.toString(16).padStart(2, "0")).join("").toUpperCase();

const rgbToOle = (r, g, b) => r + g * 256 + b * 65536;

const hexToRgb = (hex) => {
  const value = hex.replace("#", "");
  const full = value.length === 3 ? value.split("").map((c) => c + c).join("") : value;
  const int = parseInt(full, 16);
  return {
    r: (int >> 16) & 255,
    g: (int >> 8) & 255,
    b: int & 255,
  };
};

const oleToColor = (oleValue) => {
  const numeric = Number(oleValue);
  if (!Number.isFinite(numeric)) {
    throw new Error("OLE 色の値が不正です。");
  }
  const ole = numeric >>> 0;
  const r = ole & 255;
  const g = (ole >>> 8) & 255;
  const b = (ole >>> 16) & 255;
  return { hex: rgbToHex(r, g, b), ole };
};

const tupleToColor = (tuple) => {
  if (!Array.isArray(tuple) || tuple.length !== 3) {
    throw new Error("RGB の配列は 3 要素で指定してください。");
  }
  const [r, g, b] = tuple.map(clampChannel);
  return { hex: rgbToHex(r, g, b), ole: rgbToOle(r, g, b) };
};

const colorFromHexString = (value) => {
  if (typeof value !== "string") {
    throw new Error("HEX の形式が不正です。");
  }
  let hex = value.trim();
  if (!hex) {
    throw new Error("HEX の形式が不正です。");
  }
  if (/^0x[0-9a-f]+$/i.test(hex)) {
    hex = `#${hex.slice(2)}`;
  } else if (!hex.startsWith("#")) {
    hex = `#${hex}`;
  }
  if (!/^#([0-9a-f]{3}|[0-9a-f]{6})$/i.test(hex)) {
    throw new Error("HEX の形式が不正です。");
  }
  const { r, g, b } = hexToRgb(hex);
  return { hex: rgbToHex(r, g, b), ole: rgbToOle(r, g, b) };
};

const flattenColorInput = (input) => {
  const value = tryParseStructured(input);
  if (value === undefined || value === null || value === "") {
    return [];
  }

  if (typeof value === "number" && Number.isFinite(value)) {
    return [oleToColor(value)];
  }

  if (typeof value === "string") {
    const str = value.trim();
    if (!str) return [];

    if (/^#/.test(str) || /^0x[0-9a-f]+$/i.test(str) || /^[0-9a-f]{3,6}$/i.test(str)) {
      try {
        return [colorFromHexString(str)];
      } catch {
        return [];
      }
    }

    const rgbMatch = str.match(/^rgb\s*\(\s*(\d+)\s*,\s*(\d+)\s*,\s*(\d+)\s*\)$/i);
    if (rgbMatch) {
      return [tupleToColor([rgbMatch[1], rgbMatch[2], rgbMatch[3]])];
    }

    const parts = str.split(/[\s,;]+/).filter(Boolean);
    if (parts.length === 3 && parts.every((p) => /^-?\d+(?:\.\d+)?$/.test(p))) {
      try {
        return [tupleToColor(parts)];
      } catch {
        return [];
      }
    }

    if (parts.length > 1 && parts.every((p) => /^#?[0-9a-f]{3,6}$/i.test(p))) {
      return parts.flatMap((p) => flattenColorInput(p));
    }

    return [];
  }

  if (Array.isArray(value)) {
    if (value.length === 3 && value.every((v) => Number.isFinite(Number(v)))) {
      return [tupleToColor(value)];
    }
    return value.flatMap((item) => flattenColorInput(item));
  }

  if (isPlainObject(value)) {
    const results = [];
    const directKeys = [
      "hex",
      "Hex",
      "color",
      "Color",
      "colour",
      "Colour",
      "value",
      "Value",
    ];
    for (const key of directKeys) {
      if (key in value) {
        results.push(...flattenColorInput(value[key]));
      }
    }
    if ("ole" in value) {
      results.push(...flattenColorInput(value.ole));
    }
    if ("rgb" in value) {
      results.push(...flattenColorInput(value.rgb));
    }
    if ("RGB" in value) {
      results.push(...flattenColorInput(value.RGB));
    }
    if (["r", "g", "b"].every((k) => k in value)) {
      results.push(tupleToColor([value.r, value.g, value.b]));
    }
    const nestedKeys = ["colors", "Colours", "swatches", "items", "data", "list"];
    for (const key of nestedKeys) {
      if (key in value) {
        results.push(...flattenColorInput(value[key]));
      }
    }
    return results;
  }

  return [];
};

const parseColors = (value) => {
  const colors = flattenColorInput(value);
  if (!colors.length) {
    throw new Error("Color の解釈に失敗しました（HEX または RGB を指定してください）。");
  }
  const seen = new Set();
  return colors.filter((color) => {
    const key = String(color.ole);
    if (!key || seen.has(key)) return false;
    seen.add(key);
    return true;
  });
};

/* ====== Range 情報解析 ====== */
const parseRangeInfo = (value) => {
  const info = { address: "", workbook: "", sheet: "" };
  const visited = new Set();

  const assignString = (key, candidate) => {
    if (info[key]) return;
    if (typeof candidate === "string") {
      const trimmed = candidate.trim();
      if (trimmed) {
        info[key] = trimmed;
      }
    }
  };

  const inspect = (input) => {
    const parsed = tryParseStructured(input);
    if (parsed === undefined || parsed === null || parsed === "") {
      return;
    }

    if (typeof parsed === "string") {
      const trimmed = parsed.trim();
      if (trimmed && !info.address) {
        info.address = trimmed;
      }
      return;
    }

    if (typeof parsed === "number" || typeof parsed === "boolean") {
      return;
    }

    if (Array.isArray(parsed)) {
      for (const item of parsed) {
        if (info.address && info.workbook && info.sheet) break;
        inspect(item);
      }
      return;
    }

    if (!isPlainObject(parsed) || visited.has(parsed)) {
      return;
    }
    visited.add(parsed);

    assignString("address", parsed.SelectionAddress__raw);
    assignString("address", parsed.SelectionAddress);
    assignString("address", parsed.address);
    assignString("address", parsed.Address);
    assignString("address", parsed.range);
    assignString("address", parsed.Range);
    assignString("address", parsed.targetAddress);

    assignString("workbook", parsed.workbook);
    assignString("workbook", parsed.Workbook);
    assignString("workbook", parsed.workbookName);
    assignString("workbook", parsed.WorkbookName);
    assignString("workbook", parsed.book);

    assignString("sheet", parsed.sheet);
    assignString("sheet", parsed.Sheet);
    assignString("sheet", parsed.sheetName);
    assignString("sheet", parsed.SheetName);
    assignString("sheet", parsed.worksheet);
    assignString("sheet", parsed.Worksheet);

    const nestedKeys = [
      "selection",
      "Selection",
      "target",
      "Target",
      "value",
      "Value",
      "data",
      "Data",
      "range",
      "Range",
    ];
    for (const key of nestedKeys) {
      if (info.address && info.workbook && info.sheet) break;
      if (key in parsed) {
        inspect(parsed[key]);
      }
    }
  };

  inspect(value);
  return info;
};


const parseSheetPort = (value) => {
  const info = { sheet: "", workbook: "" };
  const visited = new Set();

  const assignSheet = (candidate) => {
    if (info.sheet) return;
    if (typeof candidate === "string") {
      const trimmed = candidate.trim();
      if (trimmed) {
        info.sheet = trimmed;
      }
    }
  };

  const assignWorkbook = (candidate) => {
    if (info.workbook) return;
    if (typeof candidate === "string") {
      const trimmed = candidate.trim();
      if (trimmed) {
        info.workbook = trimmed;
      }
    }
  };

  const inspect = (input) => {
    const parsed = tryParseStructured(input);
    if (parsed === undefined || parsed === null || parsed === "") {
      return;
    }

    if (typeof parsed === "string") {
      if (!info.sheet) {
        const trimmed = parsed.trim();
        if (trimmed) {
          info.sheet = trimmed;
        }
      }
      return;
    }

    if (typeof parsed === "number" || typeof parsed === "boolean") {
      return;
    }

    if (Array.isArray(parsed)) {
      for (const item of parsed) {
        if (info.sheet && info.workbook) break;
        inspect(item);
      }
      return;
    }

    if (!isPlainObject(parsed) || visited.has(parsed)) {
      return;
    }
    visited.add(parsed);

    assignSheet(parsed.sheet);
    assignSheet(parsed.Sheet);
    assignSheet(parsed.sheetName);
    assignSheet(parsed.SheetName);
    assignSheet(parsed.worksheet);
    assignSheet(parsed.Worksheet);

    assignWorkbook(parsed.workbook);
    assignWorkbook(parsed.Workbook);
    assignWorkbook(parsed.workbookName);
    assignWorkbook(parsed.WorkbookName);
    assignWorkbook(parsed.book);
    assignWorkbook(parsed.Book);

    const nestedKeys = [
      "selection",
      "Selection",
      "target",
      "Target",
      "source",
      "Source",
      "context",
      "Context",
      "sheet",
      "Sheet",
      "worksheet",
      "Worksheet",
    ];

    for (const key of nestedKeys) {
      if (info.sheet && info.workbook) break;
      if (Object.prototype.hasOwnProperty.call(parsed, key)) {
        inspect(parsed[key]);
      }
    }
  };

  inspect(value);
  return info;
};
/* ====== PowerShell スクリプト生成 ====== */
const COLOR_HELPERS = [
  "function Convert-OleToHex {",
  "  param([Parameter()][object]$Ole)",
  "  if ($null -eq $Ole) { return $null }",
  "  try {",
  "    $c = [System.Drawing.ColorTranslator]::FromOle([int]$Ole)",
  "    return ('#{0:X2}{1:X2}{2:X2}' -f $c.R, $c.G, $c.B)",
  "  } catch { return $null }",
  "}"
].join("\n");

const buildColorApplyScript = (rangeSpec, colorInt) => {
  const { addressLiteral, workbookLiteral, sheetLiteral } = rangeSpec;
  const useAddress = Boolean(addressLiteral);
  const useWorkbook = Boolean(workbookLiteral);
  const useSheet = Boolean(sheetLiteral);

  const lines = [
    "$ErrorActionPreference = 'Stop'",
    "Add-Type -AssemblyName System.Drawing",
    COLOR_HELPERS,
    "$excel = $null",
    "$workbook = $null",
    "$sheet = $null",
    "$target = $null",
    "try {",
    "  $excel = [Runtime.Interopservices.Marshal]::GetActiveObject('Excel.Application')",
    "  if ($null -eq $excel) { throw 'Excel が見つかりません。' }",
  ];

  if (useWorkbook) {
    lines.push(
      "  foreach ($wb in @($excel.Workbooks)) {",
      `    if ($null -ne $wb -and $wb.Name -eq ${workbookLiteral}) {`,
      "      $workbook = $wb",
      "      break",
      "    }",
      "  }",
      `  if ($null -eq $workbook) { throw (\"Workbook not found: {0}\" -f ${workbookLiteral}) }`
    );
  } else {
    lines.push("  $workbook = $excel.ActiveWorkbook");
  }

  lines.push("  if ($null -eq $workbook) { $workbook = $excel.ActiveWorkbook }");

  if (useSheet) {
    lines.push(
      "  if ($workbook -ne $null) {",
      "    foreach ($ws in @($workbook.Worksheets)) {",
      `      if ($null -ne $ws -and $ws.Name -eq ${sheetLiteral}) {`,
      "        $sheet = $ws",
      "        break",
      "      }",
      "    }",
      "  }",
      `  if ($null -eq $sheet) { throw (\"Worksheet not found: {0}\" -f ${sheetLiteral}) }`
    );
  } else {
    lines.push("  if ($workbook -ne $null) { $sheet = $workbook.ActiveSheet }");
  }

  lines.push("  if ($sheet -eq $null) { try { $sheet = $excel.ActiveSheet } catch { $sheet = $null } }");

  if (useAddress) {
    lines.push(
      "  if ($sheet -ne $null) {",
      "    try {",
      `      $target = $sheet.Range(${addressLiteral})`,
      "    } catch {",
      "      $target = $null",
      "    }",
      "  }",
      "  if ($target -eq $null) {",
      "    try {",
      `      $target = $excel.Range(${addressLiteral})`,
      "    } catch {",
      "      $target = $null",
      "    }",
      "  }"
    );
  }

  lines.push(
    "  if ($target -eq $null) {",
    "    $target = $excel.Selection",
    "  }",
    "  if ($null -eq $target) { throw '範囲が取得できません。' }",
    `  $target.Interior.Color = ${colorInt}`,
    "  $result = [pscustomobject]@{",
    "    ok = $true;",
    "    workbook = try { $workbook?.Name } catch { $null };",
    "    sheet = try { $sheet?.Name } catch { $null };",
    "    address = try { $target.Address() } catch { $null };",
    "    interior_color = @{",
    "      hex = Convert-OleToHex $target.Interior.Color;",
    "      ole = $target.Interior.Color",
    "    }",
    "  }",
    "} catch {",
    "  $result = [pscustomobject]@{ ok = $false; error = $_.Exception.Message }",
    "} finally {",
    "  if ($target -ne $null) { [System.Runtime.Interopservices.Marshal]::ReleaseComObject($target) | Out-Null }",
    "  if ($sheet -ne $null) { [System.Runtime.Interopservices.Marshal]::ReleaseComObject($sheet) | Out-Null }",
    "  if ($workbook -ne $null) { [System.Runtime.Interopservices.Marshal]::ReleaseComObject($workbook) | Out-Null }",
    "  if ($excel -ne $null) { [System.Runtime.Interopservices.Marshal]::ReleaseComObject($excel) | Out-Null }",
    "  [GC]::Collect()",
    "  [GC]::WaitForPendingFinalizers()",
    "}",
    "$result | ConvertTo-Json -Compress"
  );

  return lines.join("\n");
};

/* ====== SVGテンプレート ====== */
const SVG_TEMPLATE = `<svg width="100%" height="100%" viewBox="0 0 480 480" xmlns="http://www.w3.org/2000/svg">
<path d="M358.299,246.425l0,73.701l22.677,0l51.024,-48.189l0,-96.378l-25.512,-25.512l-76.535,0l62.362,62.362l-34.016,34.016Z" style="fill:#5ea3d9;"/>
<rect x="46.488" y="342.803" width="385.512" height="90.709" style="fill:#f00;"/>
</svg>`;

const createSwatchButton = (hex, ole) => {
  const btn = document.createElement("button");
  btn.type = "button";
  btn.className = "palette-swatch";
  btn.dataset.hex = hex;
  btn.dataset.ole = String(ole);
  btn.setAttribute("aria-label", `${hex} を適用`);
  btn.setAttribute("title", hex);
  btn.innerHTML = SVG_TEMPLATE.replace(/fill:#f00/gi, `fill:${hex}`);

  btn.addEventListener("click", async () => {
    btn.disabled = true;
    try {
      setStatus(`${hex} を適用中…`, "pending");
      const rangeValue = await getPortValue("Range");
      const sheetInput = await getPortValue("Sheet");
      const rangeInfo = parseRangeInfo(rangeValue);
      const sheetInfo = parseSheetPort(sheetInput);
      if (sheetInfo.sheet && !rangeInfo.sheet) {
        rangeInfo.sheet = sheetInfo.sheet;
      }
      if (sheetInfo.workbook && !rangeInfo.workbook) {
        rangeInfo.workbook = sheetInfo.workbook;
      }
      const rangeSpec = {
        addressLiteral: rangeInfo.address ? toPowerShellLiteral(rangeInfo.address) : null,
        workbookLiteral: rangeInfo.workbook ? toPowerShellLiteral(rangeInfo.workbook) : null,
        sheetLiteral: rangeInfo.sheet ? toPowerShellLiteral(rangeInfo.sheet) : null,
      };
      const script = buildColorApplyScript(rangeSpec, ole);
      const data = await requestExcelJson(script);
      if (!data?.ok) {
        throw new Error(data?.error || "PowerShell 実行に失敗しました。");
      }
      const labelParts = [data.workbook, data.sheet, data.address].filter(Boolean);
      const label = labelParts.length ? labelParts.join(" ") : "選択範囲";
      setStatus(`${label} に色を適用しました。`, "success");
    } catch (error) {
      setStatus(`着色に失敗: ${error.message}`, "error");
    } finally {
      btn.disabled = false;
    }
  });

  return btn;
};

const addColorsFromInput = (rawValue, { replace = false } = {}) => {
  const colors = parseColors(rawValue);
  if (replace) {
    grid.querySelectorAll('.palette-swatch').forEach((node) => node.remove());
    swatchSet.clear();
  }
  let added = 0;
  for (const color of colors) {
    const key = String(color.ole);
    if (swatchSet.has(key)) continue;
    const swatch = createSwatchButton(color.hex, color.ole);
    grid.insertBefore(swatch, addBtn);
    swatchSet.add(key);
    added += 1;
  }
  return { added, total: colors.length };
};

/* ====== 初期化 ====== */
const bootstrap = async () => {
  try {
    const colorInput = await getPortValue("Color");
    const { added, total } = addColorsFromInput(colorInput || "#FF0000", { replace: true });
    if (added) {
      setStatus(`${added} 色をロードしました。`, "info");
    } else if (total) {
      setStatus("すべて既に追加済みの色です。", "info");
    }
  } catch (error) {
    grid.querySelectorAll('.palette-swatch').forEach((node) => node.remove());
    swatchSet.clear();
    setStatus(`Color の解釈に失敗: ${error.message}`, "error");
  }
};

/* ====== +ボタン押下 ====== */
addBtn.addEventListener("click", async () => {
  try {
    const colorInput = await getPortValue("Color");
    const { added, total } = addColorsFromInput(colorInput || "#FF0000");
    if (added) {
      setStatus(`${added} 色を追加しました。`, "success");
    } else if (total) {
      setStatus("すべて既に追加済みの色です。", "info");
    }
  } catch (error) {
    setStatus(`Color の解釈に失敗: ${error.message}`, "error");
  }
});

bootstrap();

ui.style:
.color-palette-node {
  font-family: system-ui, -apple-system, "Segoe UI", Roboto, "Noto Sans JP", Arial, sans-serif;
  display: grid;
  gap: 8px;
}

.palette-header {
  display: flex;
  align-items: center;
  justify-content: space-between;
}

.palette-title {
  font-weight: 600;
}

.palette-status[data-state="error"] { color: #b00020; }
.palette-status[data-state="success"] { color: #137333; }
.palette-status[data-state="pending"] { color: #1a73e8; }
.palette-status[data-state="info"] { color: #5f6368; }

.palette-frame {
  border: 1px solid #e0e3e7;
  border-radius: 12px; /* 角R */
  padding: 10px;
  background: #fff;
}

.palette-grid {
  display: grid;
  grid-template-columns: repeat(6, 56px); /* 6列 */
  gap: 8px;
  align-items: start;
}

.palette-add {
  width: 56px;
  height: 56px;
  border: 1px dashed #9aa0a6;
  border-radius: 12px;
  background: #f8f9fa;
  cursor: pointer;
  display: inline-grid;
  place-items: center;
}

