ui.style:
.borderline-node {
  font-family: system-ui, -apple-system, "Segoe UI", Roboto, "Noto Sans JP", Arial, sans-serif;
  display: grid;
  gap: 8px;
}

.borderline-node .palette-header {
  display: flex;
  align-items: center;
  justify-content: space-between;
}

.borderline-node .palette-title {
  font-weight: 600;
}

.borderline-node .palette-status {
  min-height: 1.5rem;
  font-size: 0.85rem;
  line-height: 1.2;
}

.borderline-node .palette-status[data-state="error"] { color: #b00020; }
.borderline-node .palette-status[data-state="success"] { color: #137333; }
.borderline-node .palette-status[data-state="pending"] { color: #1a73e8; }
.borderline-node .palette-status[data-state="info"] { color: #5f6368; }

.borderline-node .palette-frame {
  border: 1px solid #e0e3e7;
  border-radius: 12px;
  padding: 10px;
  background: #fff;
}

.borderline-node .border-grid {
  display: grid;
  grid-template-columns: repeat(5, 56px);
  gap: 8px;
  align-items: start;
}

.borderline-node .border-swatch {
  width: 56px;
  height: 56px;
  border: 1px solid #dadce0;
  border-radius: 12px;
  background: #fff;
  padding: 0;
  display: inline-flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  transition: border-color 120ms ease, box-shadow 120ms ease, transform 120ms ease;
}

.borderline-node .border-swatch:hover:not(:disabled) {
  border-color: #9aa0a6;
  transform: translateY(-1px);
}

.borderline-node .border-swatch:focus-visible {
  outline: 2px solid #2563eb;
  outline-offset: 2px;
}

.borderline-node .border-swatch.is-active {
  border-color: #2563eb;
  box-shadow: 0 0 0 2px rgba(37, 99, 235, 0.2);
}

.borderline-node .border-swatch.is-disabled {
  opacity: 0.6;
  cursor: not-allowed;
}

.borderline-node .border-swatch img {
  display: block;
  width: 80%;
  height: 80%;
  object-fit: contain;
}
