ui.script:
const { node, controls, updateConfig, toPowerShellLiteral, helpers } = context;
if (!controls) return;

const button = controls.querySelector('[data-role="toggle"]');
const statusEl = controls.querySelector('[data-status]');
if (!button || !statusEl) return;

const status = helpers?.NodeUI?.bindStatus(statusEl);
let current = (node?.config?.IsEnabled__raw || '').toLowerCase() === 'true';

const apply = (value) => {
  current = Boolean(value);
  const label = current ? 'ON' : 'OFF';
  button.textContent = current ? 'Turn OFF' : 'Turn ON';
  status?.set?.(`Status: ${label}`, current ? 'success' : 'info');
  updateConfig('IsEnabled__raw', current ? 'true' : 'false', { silent: true });
  updateConfig('IsEnabled', toPowerShellLiteral(current ? 'true' : 'false'));
};

button.addEventListener('click', (event) => {
  event.preventDefault();
  apply(!current);
});

apply(current);

return () => {
  status?.dispose?.();
};

ui.style:
.sample-toggle-node {
  display: inline-flex;
  align-items: center;
  gap: 0.75rem;
  background: rgba(59, 130, 246, 0.12);
  padding: 0.5rem 0.75rem;
  border-radius: 0.75rem;
}
.sample-toggle-node button {
  padding: 0.3rem 0.9rem;
  border-radius: 999px;
  border: none;
  background: #2563eb;
  color: #fff;
  cursor: pointer;
}
.sample-toggle-node button:hover {
  background: #1d4ed8;
}
.sample-toggle-node [data-status] {
  font-weight: 600;
  letter-spacing: 0.05em;
}
